mapper.select:

select count(1) from susc_informes



		AND centro = #{centro}  


		AND fecha >= #{desde}  


		AND fecha <= #{hasta}


		AND (paciente_id = #{paciente_id} OR paciente_nombre like '${paciente_id}%')   


		AND (estudio_id = #{estudio_id} OR estudio_acceso = #{estudio_id})   


		AND estudio_uid = #{estudio_uid}   


		AND modalidad = #{modalidad}  


		AND informa = #{informa}  


		AND refiere = #{refiere}  


		AND icd10cm = #{icd10cm}  


		AND estado = #{estado}  


		AND centro in #{item.id}


		 AND claves like '%${item}%'

select
id, fecha, paciente_id, paciente_nombre, estudio_id, estudio_uid, estudio_acceso, modalidad, claves, texto, centro, informa, icd10cm, refiere, estado, protegido
from susc_informes
AND centro = #{centro}
AND fecha >= #{desde}
AND fecha <= #{hasta}
AND (paciente_id = #{paciente_id} OR paciente_nombre like '${paciente_id}%')
AND (estudio_id = #{estudio_id} OR estudio_acceso = #{estudio_id})
AND estudio_uid = #{estudio_uid}
AND modalidad = #{modalidad}
AND informa = #{informa}
AND refiere = #{refiere}
AND icd10cm = #{icd10cm}
AND estado = #{estado}
AND centro in #{item.id}
AND claves like '%${item}%'
order by fecha desc
LIMIT #{pageSize} OFFSET (#{pageSize} * (#{pageNumber} - 1))

select 

	id,
	fecha,
	paciente_id,
	paciente_nombre,
	estudio_id,
	estudio_uid,
	estudio_acceso,
	modalidad,
	claves,
	texto,
	centro,
	informa,
	icd10cm,
	refiere,
	estado,
	protegido

from
	susc_informes



		AND centro = #{centro}  


		AND fecha >= #{desde}  


		AND fecha <= #{hasta}


		AND (paciente_id = #{paciente_id} OR paciente_nombre like '${paciente_id}%')   


		AND (estudio_id = #{estudio_id} OR estudio_acceso = #{estudio_id})   


		AND estudio_uid = #{estudio_uid}   


		AND modalidad = #{modalidad}  


		AND informa = #{informa}  


		AND refiere = #{refiere}  


		AND icd10cm = #{icd10cm}  


		AND estado = #{estado}  


		AND centro in #{item.id}


order by fecha desc

select * from susc_informes 



		AND centro = #{centro}  


		AND fecha >= #{fecha}


		AND estado = #{estado}  


		AND centro in #{item.id}


		AND estado in #{item}



LIMIT 1 OFFSET 0

mapper.update:
